# Testing encipher
def test_encipher(rand_cipher):
    rand_cipher.encode_decode_letter = Mock(
        wraps=rand_cipher.encode_decode_letter)
    assert str(rand_cipher.encipher("ABcd eFghI JKLMN    opqrStUVwxYz")
               ) == "JPSMDOYAYYFWZRBSYOYFHCHZPS"
    assert rand_cipher.encode_decode_letter.call_count == 26


def test_encipher_null(rand_cipher):
    assert str(rand_cipher.encipher("")) == ""


# Testing decipher
def test_decipher(rand_cipher):
    rand_cipher.encode_decode_letter = Mock(
        wraps=rand_cipher.encode_decode_letter)
    assert str(rand_cipher.decipher("JPSMDOYAYYFWZRBSYOYFHCHZPS")
               ) == "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
    assert rand_cipher.encode_decode_letter.call_count == 26


# Testing encode_decode_letter
def test_invalid_char(rand_cipher):
    for i in range(0, 128):
        if (chr(i).isalpha() == False):
            with pytest.raises(ValueError) as ex:
                rand_cipher.encode_decode_letter(chr(i))
            assert ex.value.args[0] == "Please provide a letter in a-zA-Z."
    with pytest.raises(ValueError) as ex2:
        rand_cipher.encode_decode_letter("AB")
    assert ex2.value.args[0] == "Please provide a letter in a-zA-Z."


def test_encode_decode_letter_swaps(rand_cipher):
    window = rand_cipher.r_rotor.window
    offset = rand_cipher.r_rotor.offset
    tmp = Enigma(key=rand_cipher.key, rotor_order=rand_cipher.rotor_order)

    for key in rand_cipher.plugboard.swaps:
        swapped_key = rand_cipher.encode_decode_letter(key.lower())
        tmp.r_rotor.window = window
        tmp.r_rotor.offset = offset
        rand_cipher.r_rotor.window = window
        rand_cipher.r_rotor.offset = offset
        encoded_key = tmp.encode_decode_letter(
            rand_cipher.plugboard.swaps[key])
        assert swapped_key == encoded_key


def test_rotor_step(rand_cipher):
    rand_cipher.r_rotor.step = Mock(wraps=rand_cipher.r_rotor.step)
    rand_cipher.encode_decode_letter("A")
    assert rand_cipher.r_rotor.step.call_count == 1

