# Testing set_rotor_position


def test_set_rotor_position(rand_cipher, capfd):
    key = rand_cipher.key
    rand_cipher.set_rotor_position(position_key=123)
    captured = capfd.readouterr()
    assert captured.out == "Please provide a three letter position key such as AAA.\n"
    assert rand_cipher.key == key

    rand_cipher.set_rotor_position(position_key="A")
    captured = capfd.readouterr()
    assert captured.out == "Please provide a three letter position key such as AAA.\n"
    assert rand_cipher.key == key

    rand_cipher.set_rotor_position(position_key="AaA", printIt=True)
    captured = capfd.readouterr()
    assert captured.out == "Rotor position successfully updated. Now using AaA.\n"
    assert rand_cipher.key == "AaA"

    rand_cipher.set_rotor_position(position_key="aAa")
    assert rand_cipher.key == "aAa"
    assert str(rand_cipher.l_rotor.window) == "A"
    assert str(rand_cipher.m_rotor.window) == "A"
    assert str(rand_cipher.r_rotor.window) == "A"

# Testing set_rotor_order


def test_set_rotor_order(rand_cipher):
    rand_cipher.set_rotor_order(['III', 'II', 'I'])
    assert rand_cipher.l_rotor.wiring == {
        'forward': 'BDFHJLCPRTXVZNYEIWGAKMUSQO', 'backward': 'TAGBPCSDQEUFVNZHYIXJWLRKOM'}
    assert rand_cipher.m_rotor.wiring == {
        'forward': 'AJDKSIRUXBLHWTMCQGZNPYFVOE', 'backward': 'AJPCZWRLFBDKOTYUQGENHXMIVS'}
    assert rand_cipher.r_rotor.wiring == {
        'forward': 'EKMFLGDQVZNTOWYHXUSPAIBRCJ', 'backward': 'UWYGADFPVZBECKMTHXSLRINQOJ'}
    assert rand_cipher.l_rotor.prev_rotor.wiring == {
        'forward': 'AJDKSIRUXBLHWTMCQGZNPYFVOE', 'backward': 'AJPCZWRLFBDKOTYUQGENHXMIVS'}
    assert rand_cipher.m_rotor.prev_rotor.wiring == {
        'forward': 'EKMFLGDQVZNTOWYHXUSPAIBRCJ', 'backward': 'UWYGADFPVZBECKMTHXSLRINQOJ'}

# Testing set_plugs


def test_set_plugs(rand_cipher, capfd):
    rand_cipher.set_plugs(['AB', 'CD'])
    assert str(rand_cipher.plugboard) == "A <-> B\nX <-> C\nC <-> D"
    rand_cipher.set_plugs(['EF', 'GH'], replace=True, printIt=True)
    captured = capfd.readouterr()
    assert captured.out == "Plugboard successfully updated. New swaps are:\nE <-> F\nG <-> H\n"
    assert str(rand_cipher.plugboard) == "E <-> F\nG <-> H"

