# Testing step
# only testing the elif


def test_rotor_step(rand_rotor, base_rotor):
    base_rotor.next_rotor = Rotor('II', 'i', prev_rotor=base_rotor)
    base_rotor.prev_rotor = rand_rotor
    rand_rotor.next_rotor = base_rotor

    rand_rotor.next_rotor.window = 'Z'
    rand_rotor.prev_rotor = None
    rand_rotor.step()
    assert str(
        rand_rotor) == "Wiring:\n{'forward': 'VZBRGITYUPSDNHLXAWMJQOFECK', 'backward': 'QCYLXWENFTZOSMVJUDKGIARPHB'}\nWindow: Q"

    assert base_rotor.encode_letter(3, forward=False) == 6
    assert base_rotor.encode_letter(12, forward=True) == 7
    assert rand_rotor.encode_letter(8, forward=False) == 17


