def test_plugboard():
    new_plugboard = Plugboard(None)
    assert str(new_plugboard) == ""

    new_plugboard = Plugboard([])
    assert str(new_plugboard) == ""

    new_plugboard = Plugboard(["AB", "CD"])
    assert new_plugboard.swaps == {'A': 'B', 'B': 'A', 'C': 'D', 'D': 'C'}


